import React, { useEffect, useState } from 'react';

function App() {
  const [displayTime, setDisplayTime] = useState('');

  useEffect(() => {
    const fetchTime = async () => {
      try {
        const res = await fetch('/time'); // proxy handles this
        const data = await res.json();
        setDisplayTime(data.time);
      } catch (err) {
        console.error('Error fetching time:', err);
      }
    };

    fetchTime();
    const interval = setInterval(fetchTime, 10000); // every 10 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ fontSize: '48px', textAlign: 'center', marginTop: '20%' }}>
      <p>Current Warehouse Time:</p>
      <h1>{displayTime}</h1>
    </div>
  );
}

export default App;
